export const ITEMS_LOADING = 'ITEMS_LOADING';

/** These types have to do with the whole authentication 
 * and registration process and loading the User 
 */
export const USER_LOADING = 'USER_LOADING';
export const USER_LOADED = 'USER_LOADED';
export const AUTH_ERROR = 'AUTH_ERROR';
export const LOGIN_SUCCESS = 'LOGIN_SUCCESS';
export const LOGIN_FAIL = 'LOGIN_FAIL';
export const LOGOUT_SUCCESS = 'LOGOUT_SUCCESS';
export const REGISTER_SUCCESS = 'REGISTER_SUCCESS';
export const REGISTER_FAIL = 'REGISTER_FAIL';

/** These two have to do with the error reducer 
 * we want to be able to get errors put them in our state
 * and clear them if we want
*/
